import { Component } from '@angular/core';
import {Router} from "@angular/router";
import {HardCodedAuthService} from "../service/hard-coded-auth.service";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  router: Router
  auth: HardCodedAuthService
  username = ""
  password = ""
  errorMsg = "Invalid Credentials"
  invalidLogin = false

  constructor(router: Router, auth: HardCodedAuthService) {
    this.router = router
    this.auth = auth
  }

  handleLogin() {
    if (this.auth.authenticate(this.username, this.password)) {
      this.router.navigate(['dashboard', this.username])
      this.invalidLogin = false
    } else {
      this.invalidLogin = true
    }
  }
}
<div class="login-container">
  <small *ngIf="invalidLogin" class="error-message">{{errorMsg}}</small>

  <div class="form-group">
    <label for="username">Username</label>
    <input type="text" name="username" id="username" [(ngModel)]="username" class="form-control">
    <label for="password">Password</label>
    <input type="text" name="password" id="password" [(ngModel)]="password" class="form-control">
    <button (click)="handleLogin()" class="login-button">Login</button>
  </div>
</div>
