import { Component } from '@angular/core';

@Component({
  selector: 'app-todo-list',
  templateUrl: './todo-list.component.html',
  styleUrls: ['./todo-list.component.css']
})
export class TodoListComponent {

  todoList = [
    {id: 1, desc: 'Learn Angular'},
    {id: 2, desc: 'Implement todo app backend'}
  ]

  todo = {
    id: 1,
    desc: 'Learn to dance'
  }
}
<p>My Todos</p>
<table>
  <caption></caption>
  <thead>
    <tr>
      <th>Id</th>
      <th>Description</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>{{todo.id}}</td>
      <td>{{todo.desc}}</td>
    </tr>
  </tbody>
</table>
