import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username = ""
  password = ""
  errorMsg = "Invalid Credentials"
  invalidLogin = false

  handleLogin() {
    this.invalidLogin = (this.username === "admin" && this.password === "dummy") ? false : true;
  }
}
<small *ngIf="invalidLogin">{{errorMsg}}</small>

<div>
  username: <input type="text" name="username" [(ngModel)]="username">
  username: <input type="text" name="password" [(ngModel)]="password">
  <button (click)="handleLogin()">Login</button>
</div>
