import { Component } from '@angular/core';
import {ToDo} from "../model/ToDo";

@Component({
  selector: 'app-todo-list',
  templateUrl: './todo-list.component.html',
  styleUrls: ['./todo-list.component.css']
})
export class TodoListComponent {

  todoList: ToDo[]

  // todoList = [
  //   new ToDo(1, 'Learn Angular', false, this.dateFormatter()),
  //   new ToDo(2, 'Implement todo app backend', false, this.dateFormatter()),
  //   new ToDo(3, 'Implement todo app frontend', false, this.dateFormatter())
  // ]

  ngOnInit() {

  }

  dateFormatter() {
    return new Date().toLocaleDateString().replaceAll("/", "-")
  }
}
<div class="container">
  <p class="title">My Todos</p>
  <table class="todo-table">
    <caption></caption>
    <thead>
      <tr>
        <th>Description</th>
        <th>Target Date</th>
        <th>Status</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let todo of todoList">
        <td>{{todo.desc}}</td>
        <td>{{todo.targetDate}}</td>
        <td>{{todo.status}}</td>
      </tr>
    </tbody>
  </table>
</div>
