import { Component } from '@angular/core';

@Component({
  selector: 'app-error',
  templateUrl: './error.component.html',
  styleUrls: ['./error.component.css']
})
export class ErrorComponent {
  errorMsg: string;


  constructor() {
    this.errorMsg = "404";
  }
}
<div class="container">
  <p class="title">{{errorMsg}}</p>
</div>
