import { Component } from '@angular/core';
import {HardCodedAuthService} from "../service/hard-coded-auth.service";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  auth: HardCodedAuthService

  constructor(auth: HardCodedAuthService) {
    this.auth = auth
  }

}
<header class="header">
  <nav>
    <div class="logo">ToDo</div>
    <ul class="nav-list">
      <li class="nav-item"><a *ngIf="auth.isUserLoggedIn" routerLink="/dashboard/admin">Home</a></li>
      <li class="nav-item"><a *ngIf="auth.isUserLoggedIn()" routerLink="/todos">Todos</a></li>
    </ul>
    <ul class="auth-list">
      <li class="auth-item"><a *ngIf="!userLoggedIn" routerLink="/login">Login</a></li>
      <li class="auth-item"><a *ngIf="userLoggedIn" routerLink="/logout">Logout</a></li>
    </ul>
  </nav>
</header>
