import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  constructor() {
  }
}
<header class="header">
  <nav>
    <div class="logo">ToDo</div>
    <ul class="nav-list">
      <li class="nav-item"><a routerLink="/dashboard/admin">Home</a></li>
      <li class="nav-item"><a routerLink="/todos">Todos</a></li>
    </ul>
    <ul class="auth-list">
      <li class="auth-item"><a routerLink="/login">Login</a></li>
      <li class="auth-item"><a routerLink="/logout">Logout</a></li>
    </ul>
  </nav>
</header>
