import { Component } from '@angular/core';
import {HardCodedAuthService} from "../service/hard-coded-auth.service";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  userLoggedIn = false
  auth: HardCodedAuthService

  constructor(auth: HardCodedAuthService) {
    this.auth = auth
  }

  ngOnInit() {
    this.userLoggedIn = this.auth.isUserLoggedIn();
  }
}
<header class="header">
  <nav>
    <div class="logo">ToDo</div>
    <ul class="nav-list">
      <li class="nav-item"><a *ngIf="userLoggedIn" routerLink="/dashboard/admin">Home</a></li>
      <li class="nav-item"><a routerLink="/todos">Todos</a></li>
    </ul>
    <ul class="auth-list">
      <li class="auth-item"><a routerLink="/login">Login</a></li>
      <li class="auth-item"><a routerLink="/logout">Logout</a></li>
    </ul>
  </nav>
</header>
